import React, {Component, useState} from 'react';
import logo from './logo.svg';
import {Button, Form, FormGroup, FormControl, ControlLabel, Navbar, Table, ListGroup, Row, Col} from "react-bootstrap";
import {Image} from 'react-bootstrap';
import Container from 'react-bootstrap/Container';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Nav from 'react-bootstrap/Nav';
import Card from 'react-bootstrap/Card'
import CardGroup from 'react-bootstrap/CardGroup'
import 'bootstrap/dist/css/bootstrap.css';
import './App.css';
import styles from './App.css';
import ReactDOM from 'react-dom';
import {BrowserRouter, Switch, Route, Link, Redirect} from "react-router-dom";
import {useParams} from 'react-router';
import {NavLink} from 'react-router-dom';
import Cookies from 'universal-cookie';
import bcrypt from 'bcryptjs';
import openSocket from 'socket.io-client';
const socket = openSocket('http://localhost:8090');
const cookies = new Cookies();

class Home extends Component {

  state = {
    response: '',
    post: '',
    responseToPost: '',
    timestamp:''
  };
  constructor(props) {
  super(props);
  this.subscribeToTimer((err, timestamp) => console.log("test"));
}
testSocket(){
  console.log("testing");
  socket.emit('sockettest', "HAHAHAHA");
  socket.on('sockettest', test5 => console.log(test5));
}

subscribeToTimer(cb) {
socket.emit('test', "test2");
socket.on('test', test => console.log(test));
socket.on('timer', timestamp => cb(null, timestamp));
socket.emit('subscribeToTimer', 1000);
}

  handleInputChange = (event) => {
    const {value, name} = event.target;
    this.setState({[name]: value});
  }
  onSubmit = (event) => {
    event.preventDefault();
    fetch('/api/authenticate', {
      method: 'POST',
      body: JSON.stringify(this.state),
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(res => {
      if (res.status === 200) {

        this.props.history.push('/');
      } else {
        const error = new Error(res.error);
        throw error;
      }
    }).catch(err => {
      console.error(err);
      alert('Error logging in please try again');
    });
  }

  output = [];

  componentDidMount() {





    this.callApi().then(res => {
      this.setState({response: res});

    }).catch(err => console.log(err));

    this.logState();
  }

  callApi = async () => {
    const response = await fetch('/ping');
    console.log(response);
    const body = await response.json();
    if (response.status !== 200)
      throw Error(body.message);

    console.log(body);
    return body;
  };

  logState = async () => {
    console.log("state:", this.state.response);
  }

  handleSubmit = async e => {
    e.preventDefault();
    const response = await fetch('/api/world', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({post: this.state.post})
    });
    const body = await response.text();
    console.log(body);
    this.setState({responseToPost: body});
  };

  render() {
    if (!this.state.response) {
      return (<p>response was empty</p>)
    } else {
      return (<div>
        <Button onClick={this.testSocket}></Button>
        <form onSubmit={this.onSubmit}>
          <h1>Login Below!</h1>
          <input type="email" name="email" placeholder="Enter email" value={this.state.email} onChange={this.handleInputChange} required="required"/>
          <input type="password" name="password" placeholder="Enter password" value={this.state.password} onChange={this.handleInputChange} required="required"/>
          <input type="submit" value="Submit"/>
        </form>
        <ul>
          {
            this.state.response.map(username => {
              return (<li key={username.username}>{username.username}</li>)
            })
          }
