import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import styles from './App.css';
import ReactDOM from 'react-dom';
import {BrowserRouter, Switch, Route, Link} from "react-router-dom";
import { useParams } from 'react-router';
import {NavLink} from 'react-router-dom';

class Home extends Component {
  state = {
    response: '',
    post: '',
    responseToPost: ''
  };

  output = [];

  componentDidMount() {
    this.callApi().then(res => {
      console.log("res:", typeof(res));
      this.setState({response: res});

    }).catch(err => console.log(err));
    console.log(this.state.response);

    console.log(this.output);

    this.logState();
  }

  callApi = async () => {
    const response = await fetch('/ping');
    console.log(response);
    const body = await response.json();
    if (response.status !== 200)
      throw Error(body.message);

    console.log(body);
    return body;
  };

  logState = async () => {
    console.log("state:", this.state.response);
  }

  handleSubmit = async e => {
    e.preventDefault();
    const response = await fetch('/api/world', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({post: this.state.post})
    });
    const body = await response.text();

    this.setState({responseToPost: body});
  };

  render() {
    {
      console.log("aRe YoU suRe It'S tHaT oNE", this.state.response)
    }

    if (!this.state.response) {
      return (<p>response was empty</p>)
    } else {
      return (<div>

        Hello {console.log("OUTPUT OF THIS.STATE:", this.state)}
        <ul>
          {
            this.state.response.map(username => {
              return (<li key={username.username}>{username.username}</li>)
            })
          }
          <li  key={this.state.response[1].username}>{this.state.response[1].username}</li>

        </ul>
      </div>);
    }

  }
}
class App extends Component {
  render() {
    return (<div>

      <BrowserRouter>
        <li>
          <Link to="/test">test</Link>
        </li>
        <Route exact="exact" path="/" component={Home}/>
        <Route exact="exact" path="/test" component={TestPage}/>
        <Route exact="exact" path="/test/:id" component={TestPageDetails}/>
      </BrowserRouter>
    </div>)
  }

}

class TestPage extends Component {
  state = {
    response: '',
    post: '',
    responseToPost: ''
  };


  componentDidMount() {
    this.callApi().then(res => {
      console.log("res:", typeof(res));
      this.setState({response: res});

    }).catch(err => console.log(err));
    console.log(this.state.response);

  }

  callApi = async () => {
    const response = await fetch('/test');
    console.log(response);
    const body = await response.json();
    if (response.status !== 200)
      throw Error(body.message);

    console.log(body);
    return body;
  };

  render() {
    if (!this.state.response) {
      return (<p>still rendering</p>)
    } else {
      return (
        <div>
        {this.state.response.map(game => {
          return(
            <div>
            <li key={game.id}>{game.id}:<Link to={`/test/${game.id}`}>{game.player}</Link> </li>

              </div>
          )
        })}
      </div>
    )
    }
  }
}
class TestPageDetails extends Component {
  state = {
    response: '',
    post: '',
    responseToPost: ''
  };
  dice = {
    attempts: 3,
    uvalgt: 5,
    terninger: [],
    savedDice: []
  }

  componentDidMount() {
    this.callApi().then(res => {
      console.log("res:", res);
      this.setState({response: res});
        console.log(this.state.response);
        console.log(this.state.response.ones)

    }).catch(err => console.log(err));



this.rollDice();
  }
  rollDice() {
    if (this.dice.attempts==0) {
      return (window.alert("out of attempts, save your round by clicking one of the empty fields in the scoreboard"))
    }
    this.dice.terninger = [];
    console.log(typeof(this.dice.uvalgt));
    for (var i = 0; i < this.dice.uvalgt; i++) {
      this.dice.terninger.push(Math.ceil(Math.random()*6));


  }
  this.dice.attempts--;
  console.log(this.dice.attempts);

  this.forceUpdate();
}

  calculateResult() {
    var terningVerdier = this.dice.savedDice;

    // Får verdiene fra de forskjellige arrayene
    for (var i = 0; i < valgteTerninger.length; i++) {
        terningVerdier.push(valgteTerninger[i][1]);
    }

    // Resetter verdiene
    var enere = 0; var toere = 0; var treere = 0; var firere = 0; var femmere = 0; var seksere = 0;

    // Summerer alt fra enere til seksere
       for (var i = 0; i < terningVerdier.length; i++) {
            if (terningVerdier[i] == 1) {
                enere += 1;
            }
            if (terningVerdier[i] == 2) {
                toere += 2;
            }
            if (terningVerdier[i] == 3) {
                treere += 3;
            }
            if (terningVerdier[i] == 4) {
                firere += 4;
            }
            if (terningVerdier[i] == 5) {
                femmere += 5;
            }
            if (terningVerdier[i] == 6) {
                seksere += 6;
            }
        }

        var verdiAntall = [];
        verdiAntall = [enere, toere/2, treere/3, firere/4, femmere/5, seksere/6];
        var etPar = 0; var toPar = 0; var treLike = 0; var fireLike = 0; var litenStraight = 0;
        var storStraight = 0; var hus = 0; var sjanse = 0; var tabellYatzy = 0;

        // Lager array med par, resetter først
        var par = [];
        console.log(verdiAntall);
        for (var i = 0; i < verdiAntall.length; i++) {
            if (verdiAntall[i] >= 2) {
                par.push(i+1);
            }
        }
        // Et par
        if (par.length >= 1) {
            etPar = par[par.length-1]*2;
        }
        // To par
        if (par.length == 2) {
            toPar = par[0]*2+par[1]*2;
        }
        // Tre like
        for (var i = 0; i < verdiAntall.length; i++) {
            if (verdiAntall[i] >= 3) {
                y = i + 1;
                treLike = y*3;
            }
        }
        // Fire like
        for (var i = 0; i < verdiAntall.length; i++) {
            if (verdiAntall[i] >= 4) {
                y = i + 1;
                fireLike = y*4;
            }
        }
        // Liten straight
        if (Math.max(...verdiAntall) == 1 && verdiAntall[0] != 0 && verdiAntall[5] == 0) {
            litenStraight = 15;
        }
        // Stor straight
        if (Math.max(...verdiAntall) == 1 && verdiAntall[0] == 0 && verdiAntall[5] != 0) {
            storStraight = 20;
        }
        husPar = 0; husTrio = 0;
        // Hus
        if (par.length == 2 && treLike >= 1) {
            for (var i = 0; i < verdiAntall.length; i++) {
                if (verdiAntall[i] == 2) {
                    y = i + 1;
                    husPar = y * 2;
                } else if (verdiAntall[i] == 3) {
                    y = i + 1;
                    husTrio = y * 3;
                }
            } hus = husPar + husTrio;
        }
        // Sjanse
        sjanse = enere + toere + treere + firere + femmere + seksere;
        // Yatzy
        for (var i = 0; i < verdiAntall.length; i++) {
            if (verdiAntall[i] == 5) {
                tabellYatzy = 50;
            }
        }
        // Gjør slik at aksjonene kan deaktiveres
        if (enereAktivert == true) {
            enereEl.innerHTML = enere;
            enereEl.setAttribute("onClick", "javascript: poeng(enere, 'enere');");
        }
        if (toereAktivert == true) {
            toereEl.innerHTML = toere;
            toereEl.setAttribute("onClick", "javascript: poeng(toere, 'toere');");
        }
        if (treereAktivert == true) {
            treereEl.innerHTML = treere;
            treereEl.setAttribute("onClick", "javascript: poeng(treere, 'treere');");
        }
        if (firereAktivert == true) {
            firereEl.innerHTML = firere;
            firereEl.setAttribute("onClick", "javascript: poeng(firere, 'firere');");
        }
        if (femmereAktivert == true) {
            femmereEl.innerHTML = femmere;
            femmereEl.setAttribute("onClick", "javascript: poeng(femmere, 'femmere');");
        }
        if (seksereAktivert == true) {
            seksereEl.innerHTML = seksere;
            seksereEl.setAttribute("onClick", "javascript: poeng(seksere, 'seksere');");
        }
        if (etParAktivert == true) {
            etParEl.innerHTML = etPar;
            etParEl.setAttribute("onClick", "javascript: poeng(etPar, 'etPar');");
        }
        if (toParAktivert == true) {
            toParEl.innerHTML = toPar;
            toParEl.setAttribute("onClick", "javascript: poeng(toPar, 'toPar');");
        }
        if (treLikeAktivert == true) {
            treLikeEl.innerHTML = treLike;
            treLikeEl.setAttribute("onClick", "javascript: poeng(treLike, 'treLike');");
        }
        if (fireLikeAktivert == true) {
            fireLikeEl.innerHTML = fireLike;
            fireLikeEl.setAttribute("onClick", "javascript: poeng(fireLike, 'fireLike');");
        }
        if (litenStraightAktivert == true) {
            litenStraightEl.innerHTML = litenStraight;
            litenStraightEl.setAttribute("onClick", "javascript: poeng(litenStraight, 'litenStraight');");
        }
        if (storStraightAktivert == true) {
            storStraightEl.innerHTML = storStraight;
            storStraightEl.setAttribute("onClick", "javascript: poeng(storStraight, 'storStraight');");
        }
        if (husAktivert == true) {
            husEl.innerHTML = hus;
            husEl.setAttribute("onClick", "javascript: poeng(hus, 'hus');");
        }
        if (sjanseAktivert == true) {
            sjanseEl.innerHTML = sjanse;
            sjanseEl.setAttribute("onClick", "javascript: poeng(sjanse, 'sjanse');");
        }
        if (tabellYatzyAktivert == true) {
            tabellYatzyEl.innerHTML = tabellYatzy;
            tabellYatzyEl.setAttribute("onClick", "javascript: poeng(tabellYatzy, 'tabellYatzy');");
        }

  }

  saveDice(diceIndex) {
    var test = diceIndex.index;
    console.log(test);
    this.dice.savedDice.push(this.dice.terninger[diceIndex.index])
    this.dice.terninger.splice(diceIndex.index, 1);

    this.dice.uvalgt = this.dice.terninger.length;
    this.forceUpdate();
  }
  removeDice(diceIndex) {
    var test = diceIndex.index;
    console.log(test);
    this.dice.terninger.push(this.dice.savedDice[diceIndex.index])
    this.dice.savedDice.splice(diceIndex.index, 1);
    console.log("lagrede:", this.dice.savedDice);
    console.log("i spill:", this.dice.terninger)
    this.dice.uvalgt = this.dice.terninger.length;
    this.forceUpdate();
  }
  saveRound(value) {
    console.log(value);
    console.log(this.state.response[value])
    console.log(this.state.response.ones);
    if (!this.state.response[value]) {
      console.log("Cell is empty, registering round:")
    } else {
      console.log("ikke tom");
      return;
    }
  }

  callApi = async () => {
    console.log(this.props.match.params.id)
    const response = await fetch(`/test/${this.props.match.params.id}`);
    console.log(response);
    const body = await response.json();
    if (response.status !== 200)
      throw Error(body.message);

    console.log(body);
    return body;
  };

  render() {

    if (!this.state.response) {
      return (
        <p>Still loading data</p>
      )
    } else {
    return (
      <div>
      <div className="margin">
      {this.dice.terninger.map((dice, index) => {
        return (<li key={index} onClick={() => this.saveDice({index})}>{dice}</li>)
      })}
    </div>
    <button onClick={() => this.rollDice(this.state.response.ones)}>Roll dice</button>
    <div className="margin">
      {this.dice.savedDice.map((dice, index) => {
        return (<li key={index} onClick={() => this.removeDice({index})}>{dice}</li>)
      })}
    </div>

      <div id="tabell" className="table">


				<table>
          <tbody>
					<tr>
						<td className="td">Ones</td>
						<td id="ones" onClick={() => this.saveRound("ones")} className="td midstilt">{this.state.response.ones}</td>
					</tr>
          <tr>
            <td className="td">Twos</td>
            <td id="twos" className="td midstilt">{this.state.response.twos}</td>
          </tr>
          <tr>
            <td className="td">Threes</td>
            <td id="threes" className="td midstilt">{this.state.response.threes}</td>
          </tr>
          <tr>
            <td className="td">Fours</td>
            <td id="fours" className="td midstilt">{this.state.response.fours}</td>
          </tr>
          <tr>
            <td className="td">Fives</td>
            <td id="fives" className="td midstilt">{this.state.response.fives}</td>
          </tr>
          <tr>
            <td className="td">Sixes</td>
            <td id="sixes" className="td midstilt">{this.state.response.sixes}</td>
          </tr>
          <tr>
            <td className="td">One Pair</td>
            <td id="one_pair" className="td midstilt">{this.state.response.one_pair}</td>
          </tr>
          <tr>
            <td className="td">Two pairs</td>
            <td id="two_pairs" className="td midstilt">{this.state.response.two_pairs}</td>
          </tr>
          <tr>
            <td className="td">Triplets</td>
            <td id="triplet" className="td midstilt">{this.state.response.triplet}</td>
          </tr>
          <tr className="test">
            <td className="td">Four of a kind</td>
            <td id="four_of_a_kind" className="td midstilt">{this.state.response.four_of_a_kind}</td>
          </tr>
          <tr>
            <td className="td">Small Straight</td>
            <td id="small_straight" className="td midstilt">{this.state.response.small_straight}</td>
          </tr>
          <tr>
            <td className="td">Large Straight</td>
            <td id="large_straight" className="td midstilt">{this.state.response.large_straight}</td>
          </tr>
          <tr>
            <td className="td">Chance</td>
            <td id="chance" className="td midstilt">{this.state.response.chance}</td>
          </tr>
          <tr>
            <td className="td">Yatzy</td>
            <td id="yatzy" className="td midstilt">{this.state.response.yatzy}</td>
          </tr>
          <tr>
            <td className="td">Full house</td>
            <td id="full_house" className="td midstilt">{this.state.response.full_house}</td>
          </tr>
        </tbody>
				</table>
			</div>
      </div>
    );}
  }

}

export default App;
