import React, {Component, useState} from 'react';
import logo from './logo.svg';
import { Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import { Image } from 'react-bootstrap';
import Container from 'react-bootstrap/Container';
import './App.css';
import styles from './App.css';
import ReactDOM from 'react-dom';
import {BrowserRouter, Switch, Route, Link} from "react-router-dom";
import { useParams } from 'react-router';
import {NavLink} from 'react-router-dom';
import Cookies from 'universal-cookie';


class Home extends Component {
  state = {
    response: '',
    post: '',
    responseToPost: '',

  };



  handleInputChange = (event) => {
     const { value, name } = event.target;
     this.setState({
       [name]: value
     });
   }
   onSubmit = (event) => {
  event.preventDefault();
  fetch('/api/authenticate', {
    method: 'POST',
    body: JSON.stringify(this.state),
    headers: {
      'Content-Type': 'application/json'
    }
  })
  .then(res => {
    if (res.status === 200) {
      console.log(cookies.get('user'));
      this.props.history.push('/');
    } else {
      const error = new Error(res.error);
      throw error;
    }
  })
  .catch(err => {
    console.error(err);
    alert('Error logging in please try again');
  });
}




  output = [];

  componentDidMount() {
    this.callApi().then(res => {
      this.setState({response: res});

    }).catch(err => console.log(err));


    this.logState();
  }

  callApi = async () => {
    const response = await fetch('/ping');
    console.log(response);
    const body = await response.json();
    if (response.status !== 200)
      throw Error(body.message);

    console.log(body);
    return body;
  };

  logState = async () => {
    console.log("state:", this.state.response);
  }

  handleSubmit = async e => {
    e.preventDefault();
    const response = await fetch('/api/world', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({post: this.state.post})
    });
    const body = await response.text();
    console.log(body);
    this.setState({responseToPost: body});
  };


  render() {
    if (!this.state.response) {
      return (<p>response was empty</p>)
    } else {
      return (<div>

        <form onSubmit={this.onSubmit}>
        <h1>Login Below!</h1>
        <input
          type="email"
          name="email"
          placeholder="Enter email"
          value={this.state.email}
          onChange={this.handleInputChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Enter password"
          value={this.state.password}
          onChange={this.handleInputChange}
          required
        />
       <input type="submit" value="Submit"/>
      </form>
        <ul>
          {
            this.state.response.map(username => {
              return (<li key={username.username}>{username.username}</li>)
            })
          }
          <li  key={this.state.response[1].username}>{this.state.response[1].username}</li>

        </ul>
      </div>);
    }

  }
}


class Login extends Component {
  state = {
    password: '',
    email: ''
  }

  handleInputChange = (event) => {
     const { value, name } = event.target;
     this.setState({
       [name]: value
     });
   }

  onSubmit = (event) => {
 event.preventDefault();
 fetch('/api/authenticate', {
   method: 'POST',
   body: JSON.stringify(this.state),
   headers: {
     'Content-Type': 'application/json',
     'Accept': 'text/plain'
   }
 }).then((response) => response.json())
    .then((messages) => {console.log(messages.redirectUrl); this.props.history.push(messages.redirectUrl);});

}

  render() {

      return (<div>

        <form onSubmit={this.onSubmit}>
        <h1>Login Below!</h1>
        <input
          type="email"
          name="email"
          placeholder="Enter email"
          value={this.state.email}
          onChange={this.handleInputChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Enter password"
          value={this.state.password}
          onChange={this.handleInputChange}
          required
        />
       <input type="submit" value="Submit"/>
      </form>



      </div>);
    }




}

class App extends Component {
  render() {
    return (<div>

      <BrowserRouter>
        <li>
          <Link to="/login">Login</Link>
        </li>
        <Route exact="exact" path="/login" component={Login}/>
        <Route exact="exact" path="/" component={Home}/>
        <Route exact="exact" path="/gamelist/:id" component={Gamelist}/>
        <Route exact="exact" path="/game/:id" component={GameDetails}/>
      </BrowserRouter>
    </div>)
  }

}

class Gamelist extends Component {
  state = {
    response: '',
    post: '',
    responseToPost: ''
  };


  componentDidMount() {
    this.callApi().then(res => {
      console.log("res:", typeof(res));
      this.setState({response: res});

    }).catch(err => console.log(err));
    console.log(this.state.response);

  }

  callApi = async () => {
    const response = await fetch(`/gamelist/${this.props.match.params.id}`);
    console.log(response);
    const body = await response.json();
    if (response.status !== 200)
      throw Error(body.message);

    console.log(body);
    return body;
  };

  render() {
    if (!this.state.response) {
      return (<p>still rendering</p>)
    } else {
      return (
        <div>
        {this.state.response.map(game => {
          return(
            <div>
            <li key={game.id}>{game.id}:<Link to={`/game/${game.id}`}>{game.player}</Link> </li>

              </div>
          )
        })}
      </div>
    )
    }
  }
}
class GameDetails extends Component {
  state = {
    response: '',
    post: '',
    responseToPost: ''
  };
  dice = {
    attempts: 15,
    uvalgt: 5,
    terninger: [],
    savedDice: []
  }
  results = {
    ones: '',
    twos: '',
    threes: '',
    fours: '',
    fives: '',
    sixes: '',
    housePair: '',
    houseTriplet: '',
    one_pair: '',
    two_pairs: '',
    triplet: '',
    four_of_a_kind: '',
    small_straight: '',
    large_straight: '',
    house: '',
    chance: '',
    Yatzy: ''
  }
  updateVariable = {
    field: '',
    value:''
  }

  componentDidMount() {
    this.callApi().then(res => {
