import React, {Component} from 'react';
import logo from './logo.svg';
import dice1 from './1.png';
import dice2 from './2.png';
import dice3 from './3.png';
import dice4 from './4.png';
import dice5 from './5.png';
import dice6 from './6.png';
import './App.css';
import styles from './App.css';
import ReactDOM from 'react-dom';
import {BrowserRouter, Switch, Route, Link} from "react-router-dom";
import { useParams } from 'react-router';
import {NavLink} from 'react-router-dom';

class Home extends Component {
  state = {
    response: '',
    post: '',
    responseToPost: ''
  };

  output = [];

  componentDidMount() {
    this.callApi().then(res => {
      this.setState({response: res});

    }).catch(err => console.log(err));


    this.logState();
  }

  callApi = async () => {
    const response = await fetch('/ping');
    console.log(response);
    const body = await response.json();
    if (response.status !== 200)
      throw Error(body.message);

    console.log(body);
    return body;
  };

  logState = async () => {
    console.log("state:", this.state.response);
  }

  handleSubmit = async e => {
    e.preventDefault();
    const response = await fetch('/api/world', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({post: this.state.post})
    });
    const body = await response.text();
    console.log(body);
    this.setState({responseToPost: body});
  };


  render() {
    if (!this.state.response) {
      return (<p>response was empty</p>)
    } else {
      return (<div>


        <ul>
          {
            this.state.response.map(username => {
              return (<li key={username.username}>{username.username}</li>)
            })
          }
          <li  key={this.state.response[1].username}>{this.state.response[1].username}</li>

        </ul>
      </div>);
    }

  }
}
class App extends Component {
  render() {
    return (<div>

      <BrowserRouter>
        <li>
          <Link to="/test">test</Link>
        </li>
        <Route exact="exact" path="/" component={Home}/>
        <Route exact="exact" path="/test" component={TestPage}/>
        <Route exact="exact" path="/test/:id" component={TestPageDetails}/>
      </BrowserRouter>
    </div>)
  }

}

class TestPage extends Component {
  state = {
    response: '',
    post: '',
    responseToPost: ''
  };


  componentDidMount() {
    this.callApi().then(res => {
      console.log("res:", typeof(res));
      this.setState({response: res});

    }).catch(err => console.log(err));
    console.log(this.state.response);

  }

  callApi = async () => {
    const response = await fetch('/test');
    console.log(response);
    const body = await response.json();
    if (response.status !== 200)
      throw Error(body.message);

    console.log(body);
    return body;
  };

  render() {
    if (!this.state.response) {
      return (<p>still rendering</p>)
    } else {
      return (
        <div>
        {this.state.response.map(game => {
          return(
            <div>
            <li key={game.id}>{game.id}:<Link to={`/test/${game.id}`}>{game.player}</Link> </li>

              </div>
          )
        })}
      </div>
    )
    }
  }
}
class TestPageDetails extends Component {
  state = {
    response: '',
    post: '',
    responseToPost: ''
  };
  dice = {
    attempts: 15,
    uvalgt: 5,
    terninger: [],
    savedDice: []
  }
  results = {
    ones: '',
    twos: '',
    threes: '',
    fours: '',
    fives: '',
    sixes: '',
    housePair: '',
    houseTriplet: '',
    one_pair: '',
    two_pairs: '',
    triplet: '',
    four_of_a_kind: '',
    small_straight: '',
    large_straight: '',
    house: '',
    chance: '',
    Yatzy: ''
  }
  updateVariable = {
    field: '',
    value:''
  }

  componentDidMount() {
    this.callApi().then(res => {
      console.log("res:", res);
      this.setState({response: res});
        console.log(this.state.response);
        console.log(this.state.response.ones)

    }).catch(err => console.log(err));



this.rollDice();
this.calculateResult();
  }



  rollDice() {
    if (this.dice.attempts==0) {
      return (window.alert("out of attempts, save your round by clicking one of the empty fields in the scoreboard"))
    }
    this.dice.terninger = [];

    for (var i = 0; i < this.dice.uvalgt; i++) {
      this.dice.terninger.push(Math.ceil(Math.random()*6));


  }
  this.dice.attempts--;

this.calculateResult();
  this.forceUpdate();
}

  calculateResult() {
    var terningVerdier = this.dice.savedDice;

    // Resetter verdiene
    var ones = 0; var twos = 0; var threes = 0; var fours = 0; var fives = 0; var sixes = 0;
    var housePair = 0; var houseTriplet = 0; var one_pair = 0; var two_pairs = 0; var triplet = 0; var four_of_a_kind = 0; var small_straight = 0;
    var large_straight = 0; var full_house = 0; var chance = 0; var Yatzy = 0;
    this.results.ones=ones;
    this.results.twos=twos;
    this.results.threes=threes;
    this.results.fours=fours;
    this.results.fives=fives;
    this.results.sixes=sixes;
    this.results.one_pair=one_pair;
    this.results.two_pairs=two_pairs;
    this.results.triplet=triplet;
    this.results.four_of_a_kind=four_of_a_kind;
    this.results.small_straight=small_straight;
    this.results.large_straight=large_straight;
    this.results.full_house=full_house;
    this.results.chance=chance;
    this.results.Yatzy=Yatzy;

    // Summerer alt fra ones til sixes
       for (var i = 0; i < terningVerdier.length; i++) {
            if (terningVerdier[i] == 1) {
                ones += 1;
                this.results.ones=ones;
            }
            if (terningVerdier[i] == 2) {
                twos += 2;
                this.results.twos=twos;
            }
            if (terningVerdier[i] == 3) {
                threes += 3;
                this.results.threes=threes;
            }
            if (terningVerdier[i] == 4) {
                fours += 4;
                this.results.fours=fours;

            }
            if (terningVerdier[i] == 5) {
                fives += 5;
                this.results.fives=fives;
            }
            if (terningVerdier[i] == 6) {
                sixes += 6;
                this.results.sixes=sixes;
            }
        }



        var verdiAntall = [];
        verdiAntall = [ones, twos/2, threes/3, fours/4, fives/5, sixes/6];


        var par = [];

        for (var i = 0; i < verdiAntall.length; i++) {
          if (verdiAntall[i]>=2) {

            par.push(i+1);

          }
        }
        for (var i = 0; i < verdiAntall.length; i++) {

          //one pair
          if (par.length >= 1) {
            one_pair = par[par.length-1]*2;
            this.results.one_pair=one_pair;
          }
          //Two pairs
          if (par.length == 2) {
            two_pairs = par[0]*2+par[1]*2;
            this.results.two_pairs=two_pairs;
          }

          //triplets
          for (var i = 0; i < verdiAntall.length; i++) {
            if ( verdiAntall[i]>=3){
              let value = i+1;
              triplet=value*3;
              this.results.triplet=triplet;
            }
          }


        }
        // Four of a kind
        for (var i = 0; i < verdiAntall.length; i++) {
            if (verdiAntall[i] >= 4) {
                let value = i + 1;
                four_of_a_kind = value*4;
                this.results.four_of_a_kind=four_of_a_kind;
            }
        }

        // small Straight
        var counterSmall = 0;
        for (var i = 0; i < verdiAntall.length-1; i++) {
          if(verdiAntall[i]==0){

            break
          } else {
            counterSmall++
          }
          if (counterSmall == 5) {
            small_straight = 15;
            this.results.small_straight=small_straight;
          }
        }

        var counterLarge = 0;
        for (var i = 1; i < verdiAntall.length; i++) {
          if(verdiAntall[i]==0){

            break
          } else {
            counterLarge++
          }
          if (counterLarge == 5) {
            large_straight = 20;
            this.results.large_straight=large_straight;
          }
        }

        //House

        if (par.length == 2 && triplet >= 1) {
                    for (var i = 0; i < verdiAntall.length; i++) {
                        if (verdiAntall[i] == 2) {
                            let y = i + 1;
                            housePair = y * 2;

                        } else if (verdiAntall[i] == 3) {
                            let y = i + 1;
                            houseTriplet = y * 3;
                        }
                    } full_house = housePair + houseTriplet;
                      this.results.full_house=full_house;
                }

          //chance
          chance = ones + twos + threes + fours + fives + sixes;
          this.results.chance=chance;

          //Yatzy
          for (var i = 0; i < verdiAntall.length; i++) {
            if (verdiAntall[i] == 5) {
                Yatzy = 50;
                this.results.Yatzy=Yatzy;
            }

        }


  }

  saveDice(diceIndex) {
    var test = diceIndex.index;
    this.dice.savedDice.push(this.dice.terninger[diceIndex.index])
    this.dice.terninger.splice(diceIndex.index, 1);

    this.dice.uvalgt = this.dice.terninger.length;
    this.calculateResult();
    this.forceUpdate();
  }
  removeDice(diceIndex) {
    var test = diceIndex.index;
    this.dice.terninger.push(this.dice.savedDice[diceIndex.index])
    this.dice.savedDice.splice(diceIndex.index, 1);
    this.dice.uvalgt = this.dice.terninger.length;
    this.calculateResult();
    this.forceUpdate();
  }
  saveRound(value) {

    this.updateVariable.field = value;
    this.updateVariable.value = this.results[value];
    console.log(this.updateVariable)
    if (!this.state.response[value] && this.state.response[value]!=0) {
      console.log("Cell is empty, registering round:")

      fetch(`/test/${this.props.match.params.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(this.updateVariable),

      });

      {var image = "dice1";}


      this.componentDidMount();
    } else {
      console.log("ikke tom");

      return;
    }
  }

  handleSubmit = async e => {
    e.preventDefault();
    const response = await fetch(`/test/${this.props.match.params.id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({post: this.state.post})
    });
    const body = await response.text();
    console.log(body);
    this.setState({responseToPost: body});
  };

  callApi = async () => {
    console.log(this.props.match.params.id)
    const response = await fetch(`/test/${this.props.match.params.id}`);
    console.log(response);
    const body = await response.json();
    if (response.status !== 200)
      throw Error(body.message);

    console.log(body);
    return body;
  };



  render() {

    if (!this.state.response) {
      return (
        <p>Still loading data</p>
      )
    } else {
    return (

      <div className="container">
        <img src={{dice}}></img>
          <div className="diceContainer">
            <div className="margin diceLayout">
            {this.dice.terninger.map((dice, index) => {
              return (<li key={index} onClick={() => this.saveDice({index})}>{dice}</li>)
            })}
