import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import styles from './App.css';
import ReactDOM from 'react-dom';
import {BrowserRouter, Switch, Route, Link} from "react-router-dom";
import { useParams } from 'react-router';
import {NavLink} from 'react-router-dom';

class Home extends Component {
  state = {
    response: '',
    post: '',
    responseToPost: ''
  };

  output = [];

  componentDidMount() {
    this.callApi().then(res => {
      console.log("res:", typeof(res));
      this.setState({response: res});

    }).catch(err => console.log(err));
    console.log(this.state.response);

    console.log(this.output);

    this.logState();
  }

  callApi = async () => {
    const response = await fetch('/ping');
    console.log(response);
    const body = await response.json();
    if (response.status !== 200)
      throw Error(body.message);

    console.log(body);
    return body;
  };

  logState = async () => {
    console.log("state:", this.state.response);
  }

  handleSubmit = async e => {
    e.preventDefault();
    const response = await fetch('/api/world', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({post: this.state.post})
    });
    const body = await response.text();

    this.setState({responseToPost: body});
  };

  render() {
    {
      console.log("aRe YoU suRe It'S tHaT oNE", this.state.response)
    }

    if (!this.state.response) {
      return (<p>response was empty</p>)
    } else {
      return (<div>

        Hello {console.log("OUTPUT OF THIS.STATE:", this.state)}
        <ul>
          {
            this.state.response.map(username => {
              return (<li key={username.username}>{username.username}</li>)
            })
          }
          <li  key={this.state.response[1].username}>{this.state.response[1].username}</li>

        </ul>
      </div>);
    }

  }
}
class App extends Component {
  render() {
    return (<div>

      <BrowserRouter>
        <li>
          <Link to="/test">test</Link>
        </li>
        <Route exact="exact" path="/" component={Home}/>
        <Route exact="exact" path="/test" component={TestPage}/>
        <Route exact="exact" path="/test/:id" component={TestPageDetails}/>
      </BrowserRouter>
    </div>)
  }

}

class TestPage extends Component {
  state = {
    response: '',
    post: '',
    responseToPost: ''
  };


  componentDidMount() {
    this.callApi().then(res => {
      console.log("res:", typeof(res));
      this.setState({response: res});

    }).catch(err => console.log(err));
    console.log(this.state.response);

  }

  callApi = async () => {
    const response = await fetch('/test');
    console.log(response);
    const body = await response.json();
    if (response.status !== 200)
      throw Error(body.message);

    console.log(body);
    return body;
  };

  render() {
    if (!this.state.response) {
      return (<p>still rendering</p>)
    } else {
      return (
        <div>
        {this.state.response.map(game => {
          return(
            <div>
            <li key={game.id}>{game.id}:<Link to={`/test/${game.id}`}>{game.player}</Link> </li>

              </div>
          )
        })}
      </div>
    )
    }
  }
}
class TestPageDetails extends Component {
  state = {
    response: '',
    post: '',
    responseToPost: ''
  };
  dice = {
    valgt: '',
